	function setInstallerPage(inc) {

		if (p < 0) return 0;

		pArray[p].visible = false;
		p += inc;

		if (p >= pArray.length) return pArray.length;

		pArray[p].visible = true;

		backBtn.enabled = p > 0;
		backBtn.visible = p > 0;

		if (p == 1) {

			page1Lab.text = netAccess() ? 'acesso a rede: OK!\npermissão de escrita: OK!' : 'habilite a opção\n' + AE_netConfigName + '\nnas preferencias do After Effects';
		}

		if (p == 2) {

			pathTxt.text = sourceFile.exists ? decodeURI(sourceFile.fsName) : 'selecione o arquivo "O_PADEIRO_SOURCE.jsxbin"';
		}

		if (p == 0) nextBtn.enabled = true;
		if (p == 1) nextBtn.enabled = netAccess();
		if (p == 2) nextBtn.enabled = sourceFile.exists;

		installRdo.enabled = nextBtn.enabled;
		removeRdo.enabled = nextBtn.enabled;
		repairRdo.enabled = nextBtn.enabled;

		progressLab.visible = p == 3;
		cancelBtn.enabled = p < pArray.length - 1;
		nextBtn.text = p < pArray.length - 1 ? 'próximo' : 'concluir';

		if (p < 3) progressBar.value = 0;

		pathTxt.text = sourceFile.exists ? decodeURI(sourceFile.fsName) : 'selecione o arquivo "O_PADEIRO_SOURCE.jsxbin"';

		return p;
	}

